import React, { useState } from 'react'
import './style.css'

export default function Chat() {
    const [messages, setMessages] = useState([
        { from: 0, msg: 'Bienvenido al chat de maxpower' },
        { from: 0, msg: 'En que podemos ayudarte, que andas buscando' },
        { from: 1, msg: 'Buen día Maxpower, ando buscando un variador ATV' },
        { from: 1, msg: 'Necesito saber el precio y si tienen stock' }
    ])
    return (
        <div>
            <div id="smith-container">
                <div class="smith-chat-frame e5">
                    <div id="smith-chat-container">
                        <div class="smith-chat">
                            <div class="smith-chat-header background-brand">
                                <div class="smith-chat-header-overlay">
                                    <div class="smith-header-profile">
                                        <div class="smith-header-profile-avatar e2">
                                            <img src="http://api.maxpower-ar.com/imagen/logo.png" />
                                        </div>
                                        <div class="smith-header-profile-name"> MAXPOWER </div>
                                        <div class="smith-header-profile-intro">
                                            <span> Puedes comunicarte con nosotros a traves de nuestro chat online. Te atenderemos en instantes.</span>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div class="smith-chat-body">
                                <div class="smith-conversation-container">
                                    <div class="smith-conversation-body-parts">
                                        <div class="smith-conversation-parts-wrapper">
                                            <div class="smith-conversation-parts">

                                                {
                                                    messages.map((msg, i) => (
                                                        <PrintMessage msg={msg} key={i} />
                                                    ))
                                                }


                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div class="smith-chat-bar">
                        <div class="smith-chat-bar-message">
                            <textarea placeholder="Escriba su mensaje" rows="1"></textarea>
                            <button type="button" class="btn send-btn">
                                Enviar
          </button>
                        </div>
                    </div>
                </div>
            </div>
            <div id="launch" class="smith-launcher-frame e5">
                <div class="smith-launcher">
                </div>
            </div>
        </div>
    )
}


function PrintMessage({ msg }) {
    if (msg.from) {
        //Message from client
        return (
            <div class="smith-conversation-part smith-conversation-part-guest">
                <div class="smith-comment-container smith-comment-container-guest">
                    <div class="smith-comment e1 background-brand f-right">
                        <div class="smith-blocks ">
                            <div class="smith-block smith-block-paragraph" style="color: #fff;">{msg.msg}</div>
                        </div>
                    </div>
                    <div class="smith-timestamp">
                        {/* <span> time </span> <!-- Time --> */}
                    </div>
                </div>
            </div>

        )
    }

    // Message from server
    return (
        <div class="smith-conversation-part smith-conversation-part-admin">
            <div class="smith-comment-container smith-comment-container-admin">
                <div class="smith-comment-container-admin-avatar">
                    <div class="smith-avatar e2">
                        <img src="http://api.maxpower-ar.com/imagen/logo.png" />
                    </div>
